INSERT INTO `article_detail` VALUES ('1', '1', '<p>web.go</p>\n<div class=\"cnblogs_code\">\n<pre><span style=\"color: #000000\">package main\nimport (\n    </span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">fmt</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">log</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">net/http</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">strings</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">\n)\n\nfunc sayhello(w http.ResponseWriter, r </span>*<span style=\"color: #000000\">http.Request) {\n    r.ParseForm()       </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x89E3;&#x6790;&#x53C2;&#x6570;, &#x9ED8;&#x8BA4;&#x662F;&#x4E0D;&#x4F1A;&#x89E3;&#x6790;&#x7684;</span>\n    fmt.Println(r.Form) <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8FD9;&#x4E9B;&#x662F;&#x670D;&#x52A1;&#x5668;&#x7AEF;&#x7684;&#x6253;&#x5370;&#x4FE1;&#x606F;</span>\n    fmt.Println(<span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">path</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, r.URL.Path)\n    fmt.Println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">scheme</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, r.URL.Scheme)\n    fmt.Println(r.Form[</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">url_long</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">])\n    </span><span style=\"color: #0000ff\">for</span> k, v :=<span style=\"color: #000000\"> range r.Form {\n        fmt.Println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">key:</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, k)\n        fmt.Println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">val:</span><span style=\"color: #800000\">&quot;</span>, strings.Join(v, <span style=\"color: #800000\">&quot;&quot;</span><span style=\"color: #000000\">))\n    }\n    fmt.Fprintf(w, </span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">welcome to richerdyoung!</span><span style=\"color: #800000\">&quot;</span>) <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8F93;&#x51FA;&#x5230;&#x5BA2;&#x6237;&#x7AEF;&#x7684;&#x4FE1;&#x606F;</span>\n<span style=\"color: #000000\">}\n\nfunc main() {\n    http.HandleFunc(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">/</span><span style=\"color: #800000\">&quot;</span>, sayhello)       <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8BBE;&#x7F6E;&#x8BBF;&#x95EE;&#x7684;&#x8DEF;&#x7531;</span>\n    err := http.ListenAndServe(<span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">:8888</span><span style=\"color: #800000\">&quot;</span>, nil) <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8BBE;&#x7F6E;&#x76D1;&#x542C;&#x7684;&#x7AEF;&#x53E3;</span>\n    <span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\"> nil {\n        log.Fatal(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">ListenAndServe: </span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, err)\n    }\n}</span></pre>\n</div>\n<p>cmd &#x8FD0;&#x884C;</p>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201709/1103071-20170912165632938-711557655.png\" alt=\"\"></p>\n<p>&#x6253;&#x5F00;&#x6D4F;&#x89C8;&#x5668;&#xFF0C;&#x8F93;&#x5165;</p>\n<div class=\"cnblogs_code\">\n<pre>http:<spa