INSERT INTO `article_detail` VALUES ('2', '2', '<p>server.go</p>\n<div class=\"cnblogs_code\">\n<pre><span style=\"color: #000000\">package main\n \nimport (\n    </span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">net</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">fmt</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">io</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">\n)\n \n</span><span style=\"color: #0000ff\">const</span> RECV_BUF_LEN = <span style=\"color: #800080\">1024</span><span style=\"color: #000000\">\n \nfunc main() {\n    listener, err :</span>= net.Listen(<span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">tcp</span><span style=\"color: #800000\">&quot;</span>, <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">0.0.0.0:6666</span><span style=\"color: #800000\">&quot;</span>)<span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x4FA6;&#x542C;&#x5728;6666&#x7AEF;&#x53E3;</span>\n    <span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\"> nil {\n        panic(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">error listening:</span><span style=\"color: #800000\">&quot;</span>+<span style=\"color: #000000\">err.Error())\n    }\n    fmt.Println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Starting the server</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">)\n \n    </span><span style=\"color: #0000ff\">for</span><span style=\"color: #000000\"> {\n        conn, err :</span>= listener.Accept() <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x63A5;&#x53D7;&#x8FDE;&#x63A5;</span>\n        <span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\"> nil {\n            panic(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Error accept:</span><span style=\"color: #800000\">&quot;</span>+<span style=\"color: #000000\">err.Error())\n        }\n        fmt.Println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Accepted the Connection :</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, conn.RemoteAddr())\n        go EchoServer(conn)\n    }\n}\n \nfunc EchoServer(conn net.Conn) {\n    buf :</span>= make([]<span style=\"color: #0000ff\">byte</span><span style=\"color: #000000\">, RECV_BUF_LEN)\n    defer conn.Close()\n \n    </span><span style=\"color: #0000ff\">for</span><span style=\"color: #000000\"> {\n        n, err :</span>=<span style=\"color: #000000\"> conn.Read(buf);\n        </span><span style=\"color: #0000ff\">switch</span><span style=\"color: #000000\"> err {\n            </span><span style=\"color: #0000ff\">case</span><span style=\"color: #000000\"> nil:\n                conn.Write( buf[</span><span style=\"color: #800080\">0</span><span style=\"color: #000000\">:n] )\n            </span><span style=\"color: #0000ff\">case</span><span style=\"color: #000000\"> io.EOF:\n                fmt.Printf(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Warning: End of data: %s \\n</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, err);\n                </span><span style=\"color: #0000ff\">return</span>\n            <span style=\"color: #0000ff\">default</span><span style=\"color: #000000\">:\n                fmt.Printf(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Error: Reading data : %s \\n</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, err);\n                </span><span style=\"color: #0000ff\">return</span><span style=\"color: #000000\">\n        }\n     }\n}</span></pre>\n</div>\n<p>client.go</p>\n<div class=\"cnblogs_code\">\n<pre><span style=\"color: #000000\">package main\n \nimport (\n    </span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">fmt</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">time</span><span style=\"color: #800000\">&quot;</span>\n    <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">net</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">\n)\n \n</span><span style=\"color: #0000ff\">const</span> RECV_BUF_LEN = <span style=\"color: #800080\">1024</span><span style=\"color: #000000\">\n \nfunc main() {\n    conn,err :</span>= net.Dial(<span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">tcp</span><span style=\"color: #800000\">&quot;</span>, <span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">127.0.0.1:6666</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">)\n    </span><span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\"> nil {\n        panic(err.Error())\n    }\n    defer conn.Close()\n \n    buf :</span>= make([]<span style=\"color: #0000ff\">byte</span><span style=\"color: #000000\">, RECV_BUF_LEN)\n \n    </span><span style=\"color: #0000ff\">for</span> i := <span style=\"color: #800080\">0</span>; i &lt; <span style=\"color: #800080\">5</span>; i++<span style=\"color: #000000\"> {\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x51C6;&#x5907;&#x8981;&#x53D1;&#x9001;&#x7684;&#x5B57;&#x7B26;&#x4E32;</span>\n        msg := fmt.Sprintf(<span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Hello World, %03d</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, i)\n        n, err :</span>= conn.Write([]<span style=\"color: #0000ff\">byte</span><span style=\"color: #000000\">(msg))\n        </span><span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\"> nil {\n            println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Write Buffer Error:</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, err.Error())\n            </span><span style=\"color: #0000ff\">break</span><span style=\"color: #000000\">\n        }\n        fmt.Println(msg)\n \n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x4ECE;&#x670D;&#x52A1;&#x5668;&#x7AEF;&#x6536;&#x5B57;&#x7B26;&#x4E32;</span>\n        n, err =<span style=\"color: #000000\"> conn.Read(buf)\n        </span><span style=\"color: #0000ff\">if</span> err !=<span style=\"color: #000000\">nil {\n            println(</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #800000\">Read Buffer Error:</span><span style=\"color: #800000\">&quot;</span><span style=\"color: #000000\">, err.Error())\n            </span><span style=\"color: #0000ff\">break</span><span style=\"color: #000000\">\n        }\n        fmt.Println(</span><span style=\"color: #0000ff\">string</span>(buf[<span style=\"color: #800080\">0</span><span style=\"color: #000000\">:n]))\n \n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x7B49;&#x4E00;&#x79D2;&#x949F;</span>\n<span style=\"color: #000000\">        time.Sleep(time.Second)\n    }\n}</span></pre>\n</div>\n<p>&#xA0;</p>\n<p>&#x5148;&#x6267;&#x884C;server&#xFF1A;</p>\n<div class=\"cnblogs_code\">\n<pre>go run server.go</pre>\n</div>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201709/1103071-20170912152757953-2029430778.png\" alt=\"\"></p>\n<p>&#x5728;&#x6267;&#x884C;client:</p>\n<div class=\"cnblogs_code\">\n<pre>go run client.go</pre>\n</div>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201709/1103071-20170912152853907