r: #0000ff\">class</span><span style=\"color: #000000\"> Alisms\n{\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x6784;&#x9020;&#x5668;\n     *\n     * @param string $accessKeyId &#x5FC5;&#x586B;&#xFF0C;AccessKeyId\n     * @param string $accessKeySecret &#x5FC5;&#x586B;&#xFF0C;AccessKeySecret\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span> __construct(<span style=\"color: #800080\">$accessKeyId</span>, <span style=\"color: #800080\">$accessKeySecret</span><span style=\"color: #000000\">)\n    {\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;API&#x4EA7;&#x54C1;&#x540D;</span>\n        <span style=\"color: #800080\">$product</span> = &quot;Dysmsapi&quot;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;API&#x4EA7;&#x54C1;&#x57DF;&#x540D;</span>\n        <span style=\"color: #800080\">$domain</span> = &quot;dysmsapi.aliyuncs.com&quot;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x6682;&#x65F6;&#x4E0D;&#x652F;&#x6301;&#x591A;Region</span>\n        <span style=\"color: #800080\">$region</span> = &quot;cn-hangzhou&quot;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x670D;&#x52A1;&#x7ED3;&#x70B9;</span>\n        <span style=\"color: #800080\">$endPointName</span> = &quot;cn-hangzhou&quot;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x521D;&#x59CB;&#x5316;&#x7528;&#x6237;Profile&#x5B9E;&#x4F8B;</span>\n        <span style=\"color: #800080\">$profile</span> = DefaultProfile::getProfile(<span style=\"color: #800080\">$region</span>, <span style=\"color: #800080\">$accessKeyId</span>, <span style=\"color: #800080\">$accessKeySecret</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x589E;&#x52A0;&#x670D;&#x52A1;&#x7ED3;&#x70B9;</span>\n        DefaultProfile::addEndpoint(<span style=\"color: #800080\">$endPointName</span>, <span style=\"color: #800080\">$region</span>, <span style=\"color: #800080\">$product</span>, <span style=\"color: #800080\">$domain</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x521D;&#x59CB;&#x5316;AcsClient&#x7528;&#x4E8E;&#x53D1;&#x8D77;&#x8BF7;&#x6C42;</span>\n        <span style=\"color: #800080\">$this</span>-&gt;acsClient = <span style=\"color: #0000ff\">new</span> DefaultAcsClient(<span style=\"color: #800080\">$profile</span><span style=\"color: #000000\">);\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x53D1;&#x9001;&#x77ED;&#x4FE1;&#x8303;&#x4F8B;\n     *\n     * @param string $signName &lt;p&gt;\n     * &#x5FC5;&#x586B;, &#x77ED;&#x4FE1;&#x7B7E;&#x540D;&#xFF0C;&#x5E94;&#x4E25;&#x683C;&quot;&#x7B7E;&#x540D;&#x540D;&#x79F0;&quot;&#x586B;&#x5199;&#xFF0C;&#x53C2;&#x8003;&#xFF1A;&lt;a href=&quot;https://dysms.console.aliyun.com/dysms.htm#/sign&quot;&gt;&#x77ED;&#x4FE1;&#x7B7E;&#x540D;&#x9875;&lt;/a&gt;\n     * &lt;/p&gt;\n     * @param string $templateCode &lt;p&gt;\n     * &#x5FC5;&#x586B;, &#x77ED;&#x4FE1;&#x6A21;&#x677F;Code&#xFF0C;&#x5E94;&#x4E25;&#x683C;&#x6309;&quot;&#x6A21;&#x677F;CODE&quot;&#x586B;&#x5199;, &#x53C2;&#x8003;&#xFF1A;&lt;a href=&quot;https://dysms.console.aliyun.com/dysms.htm#/template&quot;&gt;&#x77ED;&#x4FE1;&#x6A21;&#x677F;&#x9875;&lt;/a&gt;\n     * (e.g. SMS_0001)\n     * &lt;/p&gt;\n     * @param string $phoneNumbers &#x5FC5;&#x586B;, &#x77ED;&#x4FE1;&#x63A5;&#x6536;&#x53F7;&#x7801; (e.g. 12345678901)\n     * @param array|null $templateParam &lt;p&gt;\n     * &#x9009;&#x586B;, &#x5047;&#x5982;&#x6A21;&#x677F;&#x4E2D;&#x5B58;&#x5728;&#x53D8;&#x91CF;&#x9700;&#x8981;&#x66FF;&#x6362;&#x5219;&#x4E3A;&#x5FC5;&#x586B;&#x9879; (e.g. Array(&quot;code&quot;=&gt;&quot;12345&quot;, &quot;product&quot;=&gt;&quot;&#x963F;&#x91CC;&#x901A;&#x4FE1;&quot;))\n     * &lt;/p&gt;\n     * @param string|null $outId [optional] &#x9009;&#x586B;, &#x53D1;&#x9001;&#x77ED;&#x4FE1;&#x6D41;&#x6C34;&#x53F7; (e.g. 1234)\n     * @return stdClass\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span> sendSms(<span style=\"color: #800080\">$signName</span>, <span style=\"color: #800080\">$templateCode</span>, <span style=\"color: #800080\">$phoneNumbers</span>, <span style=\"color: #800080\">$templateParam</span> = <span style=\"color: #0000ff\">null</span>, <span style=\"color: #800080\">$outId</span> = <span style=\"color: #0000ff\">null</span><span style=\"color: #000000\">) {\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x521D;&#x59CB;&#x5316;SendSmsRequest&#x5B9E;&#x4F8B;&#x7528;&#x4E8E;&#x8BBE;&#x7F6E;&#x53D1;&#x9001;&#x77ED;&#x4FE1;&#x7684;&#x53C2;&#x6570;</span>\n        <span style=\"color: #800080\">$request</span> = <span style=\"color: #0000ff\">new</span><span style=\"color: #000000\"> SendSmsRequest();\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x8BBE;&#x7F6E;&#x96C9;&#x77ED;&#x4FE1;&#x63A5;&#x6536;&#x53F7;&#x7801;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setPhoneNumbers(<span style=\"color: #800080\">$phoneNumbers</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x8BBE;&#x7F6E;&#x7B7E;&#x540D;&#x540D;&#x79F0;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setSignName(<span style=\"color: #800080\">$signName</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x8BBE;&#x7F6E;&#x6A21;&#x677F;CODE</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setTemplateCode(<span style=\"color: #800080\">$templateCode</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x53EF;&#x9009;&#xFF0C;&#x8BBE;&#x7F6E;&#x6A21;&#x677F;&#x53C2;&#x6570;</span>\n        <span style=\"color: #0000ff\">if</span>(<span style=\"color: #800080\">$templateParam</span><span style=\"color: #000000\">) {\n            </span><span style=\"color: #800080\">$request</span>-&gt;setTemplateParam(json_encode(<span style=\"color: #800080\">$templateParam</span><span style=\"color: #000000\">));\n        }\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x53EF;&#x9009;&#xFF0C;&#x8BBE;&#x7F6E;&#x6D41;&#x6C34;&#x53F7;</span>\n        <span style=\"color: #0000ff\">if</span>(<span style=\"color: #800080\">$outId</span><span style=\"color: #000000\">) {\n            </span><span style=\"color: #800080\">$request</span>-&gt;setOutId(<span style=\"color: #800080\">$outId</span><span style=\"color: #000000\">);\n        }\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x53D1;&#x8D77;&#x8BBF;&#x95EE;&#x8BF7;&#x6C42;</span>\n        <span style=\"color: #800080\">$acsResponse</span> = <span style=\"color: #800080\">$this</span>-&gt;acsClient-&gt;getAcsResponse(<span style=\"color: #800080\">$request</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x6253;&#x5370;&#x8BF7;&#x6C42;&#x7ED3;&#x679C;\n        // var_dump($acsResponse);</span>\n\n        <span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$acsResponse</span><span style=\"color: #000000\">;\n\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x67E5;&#x8BE2;&#x77ED;&#x4FE1;&#x53D1;&#x9001;&#x60C5;&#x51B5;&#x8303;&#x4F8B;\n     *\n     * @param string $phoneNumbers &#x5FC5;&#x586B;, &#x77ED;&#x4FE1;&#x63A5;&#x6536;&#x53F7;&#x7801; (e.g. 12345678901)\n     * @param string $sendDate &#x5FC5;&#x586B;&#xFF0C;&#x77ED;&#x4FE1;&#x53D1;&#x9001;&#x65E5;&#x671F;&#xFF0C;&#x683C;&#x5F0F;Ymd&#xFF0C;&#x652F;&#x6301;&#x8FD1;30&#x5929;&#x8BB0;&#x5F55;&#x67E5;&#x8BE2; (e.g. 20170710)\n     * @param int $pageSize &#x5FC5;&#x586B;&#xFF0C;&#x5206;&#x9875;&#x5927;&#x5C0F;\n     * @param int $currentPage &#x5FC5;&#x586B;&#xFF0C;&#x5F53;&#x524D;&#x9875;&#x7801;\n     * @param string $bizId &#x9009;&#x586B;&#xFF0C;&#x77ED;&#x4FE1;&#x53D1;&#x9001;&#x6D41;&#x6C34;&#x53F7; (e.g. abc123)\n     * @return stdClass\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span> queryDetails(<span style=\"color: #800080\">$phoneNumbers</span>, <span style=\"color: #800080\">$sendDate</span>, <span style=\"color: #800080\">$pageSize</span> = 10, <span style=\"color: #800080\">$currentPage</span> = 1, <span style=\"color: #800080\">$bizId</span>=<span style=\"color: #0000ff\">null</span><span style=\"color: #000000\">) {\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x521D;&#x59CB;&#x5316;QuerySendDetailsRequest&#x5B9E;&#x4F8B;&#x7528;&#x4E8E;&#x8BBE;&#x7F6E;&#x77ED;&#x4FE1;&#x67E5;&#x8BE2;&#x7684;&#x53C2;&#x6570;</span>\n        <span style=\"color: #800080\">$request</span> = <span style=\"color: #0000ff\">new</span><span style=\"color: #000000\"> QuerySendDetailsRequest();\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x77ED;&#x4FE1;&#x63A5;&#x6536;&#x53F7;&#x7801;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setPhoneNumber(<span style=\"color: #800080\">$phoneNumbers</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x9009;&#x586B;&#xFF0C;&#x77ED;&#x4FE1;&#x53D1;&#x9001;&#x6D41;&#x6C34;&#x53F7;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setBizId(<span style=\"color: #800080\">$bizId</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x77ED;&#x4FE1;&#x53D1;&#x9001;&#x65E5;&#x671F;&#xFF0C;&#x652F;&#x6301;&#x8FD1;30&#x5929;&#x8BB0;&#x5F55;&#x67E5;&#x8BE2;&#xFF0C;&#x683C;&#x5F0F;Ymd</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setSendDate(<span style=\"color: #800080\">$sendDate</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x5206;&#x9875;&#x5927;&#x5C0F;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setPageSize(<span style=\"color: #800080\">$pageSize</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x5FC5;&#x586B;&#xFF0C;&#x5F53;&#x524D;&#x9875;&#x7801;</span>\n        <span style=\"color: #800080\">$request</span>-&gt;setCurrentPage(<span style=\"color: #800080\">$currentPage</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x53D1;&#x8D77;&#x8BBF;&#x95EE;&#x8BF7;&#x6C42;</span>\n        <span style=\"color: #800080\">$acsResponse</span> = <span style=\"color: #800080\">$this</span>-&gt;acsClient-&gt;getAcsResponse(<span style=\"color: #800080\">$request</span><span style=\"color: #000000\">);\n\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x6253;&#x5370;&#x8BF7;&#x6C42;&#x7ED3;&#x679C;\n        // var_dump($acsResponse);</span>\n\n        <span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$acsResponse</span><span style=\"color: #000000\">;\n    }\n\n}</span></pre>\n</div>\n<p>&#xA0;</p>\n<p>&#xA0;3 &#x8C03;&#x7528;&#x5B9E;&#x4F8B;&#xFF1A; &#x7EFF;&#x8272;&#x90E8;&#x5206;&#x4FEE;&#x6539;&#x8FC7;&#xFF0C;&#x8981;&#x6839;&#x636E;&#x4F60;&#x77ED;&#x4FE1;&#x6A21;&#x677F;&#x7684;&#x503C;&#x6765;&#x6539;</p>\n<div class=\"cnblogs_code\">\n<pre><span style=\"color: #008000\">/*</span><span style=\"color: #008000\"> &#x53D1;&#x9001;&#x624B;&#x673A;&#x9A8C;&#x8BC1;&#x7801; </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">require_once</span> (ROOT_PATH . &apos;sms/Alisms.class.php&apos;<span style=\"color: #000000\">);\n    </span><span style=\"color: #008080\">header</span>(&apos;Content-Type: text/plain; charset=utf-8&apos;<span style=\"color: #000000\">);\n    </span><span style=\"color: #800080\">$demo</span> = <span style=\"color: #0000ff\">new</span><span style=\"color: #000000\"> Alisms(\n        </span>&quot;&quot;,  <span style=\"color: #008000\">// AccessKeyID\n</span>        &quot;&quot;   //AccessKeySecret<span style=\"color: #000000\">\n    );\n    </span><span style=\"color: #800080\">$mobile_code</span> =&quot;&quot;; <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x9A8C;&#x8BC1;&#x7801;</span>\n    <span style=\"color: #800080\">$response</span> = <span style=\"color: #800080\">$demo</span>-&gt;<span style=\"color: #000000\">sendSms(\n        </span>&quot;&quot;, <span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;&#x7B7E;&#x540D;</span>\n        &quot;&quot;, <span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;&#x6A21;&#x677F;&#x7F16;&#x53F7;</span>\n        &quot;&quot;, <span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;&#x63A5;&#x6536;&#x8005;,&#x591A;&#x4E2A;&#x624B;&#x673A;&#x53F7;&#x7528;&#x82F1;&#x6587;&#x9017;&#x53F7;&#x9694;&#x5F00;</span>\n        <span style=\"color: #0000ff\">Array</span>(  <span style=\"color: #008000\">//</span><span style=\"color: #008000\"> &#x77ED;&#x4FE1;&#x6A21;&#x677F;&#x4E2D;&#x5B57;&#x6BB5;&#x7684;&#x503C;</span>\n            &quot;<span style=\"background-color: #00ff00\">number</span>&quot;=&gt;<span style=\"color: #800080\">$mobile_code</span><span style=\"color: #000000\">\n        )</span>,\n        &quot;123&quot;<span style=\"color: #000000\">\n    );\n\n    </span><span style=\"color: #800080\">$result</span> = <span style=\"color: #800080\">$response</span>-&gt;<span style=\"color: #000000\">Code;\n    </span><span style=\"color: #0000ff\">if</span>(<span style=\"color: #800080\">$result</span> == &apos;OK&apos;){<br>       echo &quot;success&quot;;<br>&#x3000;&#x3000;}else{<br>       echo &quot;fail&quot;;<br>&#x3000;&#x3000;}</pre>\n</div>\n<p>&#xA0;</p>');
INSERT INTO `article_detail` VALUES ('11', '11', '<p>1 apache&#xA0;</p>\n<div>\n<div>&#x9996;&#x5148;Mac&#x7CFB;&#x7EDF;&#x662F;&#x81EA;&#x5E26;&#x4E86;Apache&#xFF0C;&#x53EA;&#x9700;&#x8981;&#x6267;&#x884C;</div>\n<div>\n<div class=\"cnblogs_code\">\n<pre>sudo apachectl start</pre>\n</div>\n</div>\n<div>&#x5C31;&#x53EF;&#x4EE5;&#x6253;&#x5F00;Apache&#x670D;&#x52A1;&#xFF0C;&#x7136;&#x540E;&#x8BBF;&#x95EE; <a href=\"http://localhost/\" target=\"_blank\">http://localhost</a> &#x5C31;&#x53EF;&#x4EE5;&#x8BBF;&#x95EE;&#x5230;&#xFF0C;<code>it&apos;s works</code><br>&#x4F46;&#x662F;Apache&#x9ED8;&#x8BA4;&#x7684;&#x76EE;&#x5F55;&#x5728; &#xA0; &#xA0;</div>\n<div>\n<div class=\"cnblogs_code\">\n<pre>/Library/WebServer/Documents/</pre>\n</div>\n</div>\n<div>&#x60F3;&#x5207;&#x6362;&#x5230;&#x81EA;&#x5DF1;&#x7684;&#x76EE;&#x5F55;&#x4E0B;&#xFF0C;&#x6BD4;&#x5982;&#xA0;</div>\n<div>\n<div class=\"cnblogs_code\">\n<pre>/Users/username/wwwroot</pre>\n</div>\n<p>&#x4FEE;&#x6539;Apache&#x7684;&#x914D;&#x7F6E;&#x6587;&#x4EF6;</p>\n<div class=\"cnblogs_code\">\n<pre>sudo vim /etc/apache2/httpd.conf</pre>\n</div>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201708/1103071-20170814200525053-1636977730.png\" alt=\"\"></p>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201708/1103071-20170814200542053-220837410.png\" alt=\"\"></p>\n<p>&#x5C06; &#xA0;mod_rewrite &#x548C; &#xA0;libphp5 &#x524D;&#x9762;&#x7684;&#x6CE8;&#x91CA;&#x53BB;&#x6389;<br>&#x627E;&#x5230; DocumentRoot &#x548C; Directory &#x66FF;&#x6362;&#x6210;&#xA0;<code>/Users/username/wwwroot</code><br>&#x4FDD;&#x5B58;&#xFF0C;&#x7136;&#x540E;&#x91CD;&#x542F;&#xA0;</p>\n<div class=\"cnblogs_code\">\n<pre>sudo apachectl restart</pre>\n</div>\n<p>&#xA0;</p>\n<p>2 PHP&#xA0;</p>\n<p>&#x4F7F;&#x7528;&#xA0;<code>php -version</code>&#xA0;&#x67E5;&#x770B;&#x5F53;&#x524D;PHP&#x7684;&#x60C5;&#x51B5;</p>\n<p><img src=\"http://images2017.cnblogs.com/blog/1103071/201708/1103071-20170814200706709-1308512348.png\" alt=\"\"></