INSERT INTO `article_detail` VALUES ('31', '31', '<p>1 &#x627E;&#x5230;&#x6587;&#x4EF6;&#xFF1A;/thinkphp/library/think/paginator/driver/Bootstrap.php</p>\n<div class=\"cnblogs_code\">\n<pre>&lt;?<span style=\"color: #000000\">php\n</span><span style=\"color: #008000\">//</span><span style=\"color: #008000\"> +----------------------------------------------------------------------\n// | ThinkPHP [ WE CAN DO IT JUST THINK ]\n// +----------------------------------------------------------------------\n// | Copyright (c) 2006~2017 http://thinkphp.cn All rights reserved.\n// +----------------------------------------------------------------------\n// | Licensed ( http://www.apache.org/licenses/LICENSE-2.0 )\n// +----------------------------------------------------------------------\n// | Author: zhangyajun &lt;448901948@qq.com&gt;\n// +----------------------------------------------------------------------</span>\n<span style=\"color: #000000\">\nnamespace think\\paginator\\driver;\n\n</span><span style=\"color: #0000ff\">use</span><span style=\"color: #000000\"> think\\Paginator;\n\n</span><span style=\"color: #0000ff\">class</span> Bootstrap <span style=\"color: #0000ff\">extends</span><span style=\"color: #000000\"> Paginator\n{\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">\n     * &#x4E0A;&#x4E00;&#x9875;&#x6309;&#x94AE;\n     * @param string $text\n     * @return string\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">protected</span> <span style=\"color: #0000ff\">function</span> getPreviousButton(<span style=\"color: #800080\">$text</span> = &quot;&#x4E0A;&#x4E00;&#x9875;&quot;<span style=\"color: #000000\">)\n    {\n\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$this</span>-&gt;currentPage() &lt;= 1<span style=\"color: #000000\">) {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getDisabledTextWrapper(<span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n        }\n\n        </span><span style=\"color: #800080\">$url</span> = <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">url(\n            </span><span style=\"color: #800080\">$this</span>-&gt;currentPage() - 1<span style=\"color: #000000\">\n        );\n\n        </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getPageLinkWrapper(<span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x4E0B;&#x4E00;&#x9875;&#x6309;&#x94AE;\n     * @param string $text\n     * @return string\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">protected</span> <span style=\"color: #0000ff\">function</span> getNextButton(<span style=\"color: #800080\">$text</span> = &apos;&#x4E0B;&#x4E00;&#x9875;&apos;<span style=\"color: #000000\">)\n    {\n        </span><span style=\"color: #0000ff\">if</span> (!<span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">hasMore) {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getDisabledTextWrapper(<span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n        }\n\n        </span><span style=\"color: #800080\">$url</span> = <span style=\"color: #800080\">$this</span>-&gt;url(<span style=\"color: #800080\">$this</span>-&gt;currentPage() + 1<span style=\"color: #000000\">);\n\n        </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getPageLinkWrapper(<span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n    }\n\n     </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x9996;&#x9875;&#x6309;&#x94AE;\n     * @param string $text\n     * @return string\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">protected</span> <span style=\"color: #0000ff\">function</span> getFirstButton(<span style=\"color: #800080\">$text</span> = &apos;&#x9996;&#x9875;&apos;<span style=\"color: #000000\">)\n    {\n        </span><span style=\"color: #800080\">$nowPage</span> = <span style=\"color: #800080\">$this</span>-&gt;rollPage;<span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8BA1;&#x7B97;&#x5206;&#x9875;&#x4E34;&#x65F6;&#x53D8;&#x91CF;\n        //&#x5F53;  &#x603B;&#x9875;&#x6570;&#x5927;&#x4E8E;&#x5B9A;&#x4E49;&#x7684;&#x9875;&#x6570;&#x65F6;  &#x4E14;  &#x5F53;&#x524D;&#x9875;&#x6570;&#x5927;&#x4E8E;&#x524D;&#x51E0;&#x9875;&#x65F6;  &#x663E;&#x793A;&#x9996;&#x9875;</span>\n        <span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$this</span>-&gt;lastPage &gt; <span style=\"color: #800080\">$this</span>-&gt;showPage &amp;&amp; <span style=\"color: #800080\">$this</span>-&gt;currentPage &gt; <span style=\"color: #800080\">$nowPage</span><span style=\"color: #000000\">) {\n            \n            </span><span style=\"color: #800080\">$url</span> = <span style=\"color: #800080\">$this</span>-&gt;url(1<span style=\"color: #000000\">);\n            \n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getPageLinkWrapper(<span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n        }\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x672B;&#x9875;&#x6309;&#x94AE;\n     * @param string $text\n     * @return string\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">protected</span> <span style=\"color: #0000ff\">function</span> getLastButton(<span style=\"color: #800080\">$text</span> = &apos;&#x672B;&#x9875;&apos;<span style=\"color: #000000\">)\n    {\n        </span><span style=\"color: #800080\">$nowPage</span> = <span style=\"color: #800080\">$this</span>-&gt;rollPage;<span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8BA1;&#x7B97;&#x5206;&#x9875;&#x4E34;&#x65F6;&#x53D8;&#x91CF;\n        \n        //&#x5F53;  &#x603B;&#x9875;&#x6570;&#x5927;&#x4E8E;&#x5B9A;&#x4E49;&#x7684;&#x9875;&#x6570;&#x65F6;  &#x4E14;  &#x5F53;&#x524D;&#x9875;&#x6570;&#x5C0F;&#x4E8E;&#x6700;&#x540E;&#x7684;&#x51E0;&#x9875;&#x65F6;  &#x663E;&#x793A;&#x672B;&#x9875;</span>\n        <span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$this</span>-&gt;lastPage &gt; <span style=\"color: #800080\">$this</span>-&gt;showPage &amp;&amp; <span style=\"color: #800080\">$this</span>-&gt;currentPage &lt; (<span style=\"color: #800080\">$this</span>-&gt;lastPage - <span style=\"color: #800080\">$nowPage</span><span style=\"color: #000000\">)) {\n            \n            </span><span style=\"color: #800080\">$url</span> = <span style=\"color: #800080\">$this</span>-&gt;url(<span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">lastPage);\n            \n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$this</span>-&gt;getPageLinkWrapper(<span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$text</span><span style=\"color: #000000\">);\n        }\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x9875;&#x7801;&#x6309;&#x94AE;\n     * @return string\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">protected</span> <span style=\"color: #0000ff\">function</span><span style=\"color: #000000\"> getLinks()\n    {\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">simple)\n            </span><span style=\"color: #0000ff\">return</span> &apos;&apos;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #800080\">$block</span> =<span style=\"color: #000000\"> [\n            </span>&apos;first&apos;  =&gt; <span style=\"color: #0000ff\">null</span>,\n            &apos;slider&apos; =&gt; <span style=\"color: #0000ff\">null</span>,\n            &apos;last&apos;   =&gt; <span style=\"color: #0000ff\">null</span><span style=\"color: #000000\">\n        ];\n\n        </span><span style=\"color: #800080\">$side</span>   = 3<span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$window</span> = <span style=\"color: #800080\">$side</span> * 2<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$this</span>-&gt;lastPage &lt; <span style=\"color: #800080\">$window</span> + 6<span style=\"color: #000000\">) {\n            </span><span style=\"color: #800080\">$block</span>[&apos;first&apos;] = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(1, <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">lastPage);\n        } </span><span style=\"color: #0000ff\">elseif</span> (<span style=\"color: #800080\">$this</span>-&gt;currentPage &lt;= <span style=\"color: #800080\">$window</span><span style=\"color: #000000\">) {\n            </span><span style=\"color: #800080\">$block</span>[&apos;first&apos;] = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(1, <span style=\"color: #800080\">$window</span> + 2<span style=\"color: #000000\">);\n            </span><span style=\"color: #800080\">$block</span>[&apos;last&apos;]  = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(<span style=\"color: #800080\">$this</span>-&gt;lastPage - 1, <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">lastPage);\n        } </span><span style=\"color: #0000ff\">elseif</span> (<span style=\"color: #800080\">$this</span>-&gt;currentPage &gt; (<span style=\"color: #800080\">$this</span>-&gt;lastPage - <span style=\"color: #800080\">$window</span><span style=\"color: #000000\">)) {\n            </span><span style=\"color: #800080\">$block</span>[&apos;first&apos;] = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(1, 2<span style=\"color: #000000\">);\n            </span><span style=\"color: #800080\">$block</span>[&apos;last&apos;]  = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(<span style=\"color: #800080\">$this</span>-&gt;lastPage - (<span style=\"color: #800080\">$window</span> + 2), <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">lastPage);\n        } </span><span style=\"color: #0000ff\">else</span><span style=\"color: #000000\"> {\n            </span><span style=\"color: #800080\">$block</span>[&apos;first&apos;]  = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(1, 2<span style=\"color: #000000\">);\n            </span><span style=\"color: #800080\">$block</span>[&apos;slider&apos;] = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(<span style=\"color: #800080\">$this</span>-&gt;currentPage - <span style=\"color: #800080\">$side</span>, <span style=\"color: #800080\">$this</span>-&gt;currentPage + <span style=\"color: #800080\">$side</span><span style=\"color: #000000\">);\n            </span><span style=\"color: #800080\">$block</span>[&apos;last&apos;]   = <span style=\"color: #800080\">$this</span>-&gt;getUrlRange(<span style=\"color: #800080\">$this</span>-&gt;lastPage - 1, <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">lastPage);\n        }\n\n        </span><span style=\"color: #800080\">$html</span> = &apos;&apos;<span style=\"color: #000000\">;\n\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #008080\">is_array</span>(<span style=\"color: #800080\">$block</span>[&apos;first&apos;<span style=\"color: #000000\">])) {\n            </span><span style=\"color: #800080\">$html</span> .= <span style=\"color: #800080\">$this</span>-&gt;getUrlLinks(<span style=\"color: #800080\">$block</span>[&apos;first&apos;<span style=\"color: #000000\">]);\n        }\n\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #008080\">is_array</span>(<span style=\"color: #800080\">$block</span>[&apos;slider&apos;<span style=\"color: #000000\">])) {\n            </span><span style=\"color: #800080\">$html</span> .= <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">getDots();\n            </span><span style=\"color: #800080\">$html</span> .= <span style=\"color: #800080\">$this</span>-&gt;getUrlLinks(<span style=\"color: #800080\">$block</span>[&apos;slider&apos;<span style=\"color: #000000\">]);\n        }\n\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #008080\">is_array</span>(<span style=\"color: #800080\">$block</span>[&apos;last&apos;<span style=\"color: #000000\">])) {\n            </span><span style=\"color: #800080\">$html</span> .= <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">getDots();\n            </span><span style=\"color: #800080\">$html</span> .= <span style=\"color: #800080\">$this</span>-&gt;getUrlLinks(<span style=\"color: #800080\">$block</span>[&apos;last&apos;<span style=\"color: #000000\">]);\n        }\n\n        </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$html</span><span style=\"color: #000000\">;\n    }\n\n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x6E32;&#x67D3;&#x5206;&#x9875;html\n     * @return mixed\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</spa