---------</span>\n<span style=\"color: #000000\">\nnamespace Wechat\\Controller;\n</span><span style=\"color: #0000ff\">use</span><span style=\"color: #000000\"> Wechat\\Controller\\WechatController;\n\n</span><span style=\"color: #0000ff\">class</span> PushController <span style=\"color: #0000ff\">extends</span><span style=\"color: #000000\"> WechatController {\n    \n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x53D1;&#x9001;post&#x8BF7;&#x6C42;\n     * @param string $url\n     * @param string $param\n     * @return bool|mixed\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">function</span> request_post(<span style=\"color: #800080\">$url</span> = &apos;&apos;, <span style=\"color: #800080\">$param</span> = &apos;&apos;<span style=\"color: #000000\">)\n    {\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #0000ff\">empty</span>(<span style=\"color: #800080\">$url</span>) || <span style=\"color: #0000ff\">empty</span>(<span style=\"color: #800080\">$param</span><span style=\"color: #000000\">)) {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #0000ff\">false</span><span style=\"color: #000000\">;\n        }\n        </span><span style=\"color: #800080\">$postUrl</span> = <span style=\"color: #800080\">$url</span><span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$curlPost</span> = <span style=\"color: #800080\">$param</span><span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$ch</span> = curl_init(); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x521D;&#x59CB;&#x5316;curl</span>\n        curl_setopt(<span style=\"color: #800080\">$ch</span>, CURLOPT_URL, <span style=\"color: #800080\">$postUrl</span>); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x6293;&#x53D6;&#x6307;&#x5B9A;&#x7F51;&#x9875;</span>\n        curl_setopt(<span style=\"color: #800080\">$ch</span>, CURLOPT_HEADER, 0); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8BBE;&#x7F6E;header</span>\n        curl_setopt(<span style=\"color: #800080\">$ch</span>, CURLOPT_RETURNTRANSFER, 1); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8981;&#x6C42;&#x7ED3;&#x679C;&#x4E3A;&#x5B57;&#x7B26;&#x4E32;&#x4E14;&#x8F93;&#x51FA;&#x5230;&#x5C4F;&#x5E55;&#x4E0A;</span>\n        curl_setopt(<span style=\"color: #800080\">$ch</span>, CURLOPT_POST, 1); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">post&#x63D0;&#x4EA4;&#x65B9;&#x5F0F;</span>\n        curl_setopt(<span style=\"color: #800080\">$ch</span>, CURLOPT_POSTFIELDS, <span style=\"color: #800080\">$curlPost</span><span style=\"color: #000000\">);\n        </span><span style=\"color: #800080\">$data</span> = curl_exec(<span style=\"color: #800080\">$ch</span>); <span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x8FD0;&#x884C;curl</span>\n        curl_close(<span style=\"color: #800080\">$ch</span><span style=\"color: #000000\">);\n        </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$data</span><span style=\"color: #000000\">;\n    }\n \n \n    </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x53D1;&#x9001;get&#x8BF7;&#x6C42;\n     * @param string $url\n     * @return bool|mixed\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">function</span> request_get(<span style=\"color: #800080\">$url</span> = &apos;&apos;<span style=\"color: #000000\">)\n    {\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #0000ff\">empty</span>(<span style=\"color: #800080\">$url</span><span style=\"color: #000000\">)) {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #0000ff\">false</span><span style=\"color: #000000\">;\n        }\n        </span><span style=\"color: #800080\">$ch</span> =<span style=\"color: #000000\"> curl_init();\n        curl_setopt(</span><span style=\"color: #800080\">$ch</span>, CURLOPT_URL, <span style=\"color: #800080\">$url</span><span style=\"color: #000000\">);\n        curl_setopt(</span><span style=\"color: #800080\">$ch</span>, CURLOPT_RETURNTRANSFER, 1<span style=\"color: #000000\">);\n        </span><span style=\"color: #800080\">$data</span> = curl_exec(<span style=\"color: #800080\">$ch</span><span style=\"color: #000000\">);\n        curl_close(</span><span style=\"color: #800080\">$ch</span><span style=\"color: #000000\">);\n        </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #800080\">$data</span><span style=\"color: #000000\">;\n    }\n    \n     </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * @param $appid\n     * @param $appsecret\n     * @return mixed\n     * &#x83B7;&#x53D6;token\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span><span style=\"color: #000000\"> get_token(){\n        </span><span style=\"color: #800080\">$appid</span> = &apos;&apos;<span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$secret</span> = &quot;&quot;<span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$url2</span> = &quot;https://api.weixin.qq.com/cgi-bin/token?grant_type=client_credential&amp;appid=&quot; . <span style=\"color: #800080\">$appid</span> . &quot;&amp;secret=&quot; . <span style=\"color: #800080\">$secret</span> . &quot;&quot;<span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$result</span> = json_decode(<span style=\"color: #008080\">file_get_contents</span>(<span style=\"color: #800080\">$url2</span>),<span style=\"color: #0000ff\">true</span><span style=\"color: #000000\">);\n        </span><span style=\"color: #800080\">$access_token</span> = <span style=\"color: #800080\">$result</span>[&apos;access_token&apos;<span style=\"color: #000000\">];\n        </span><span style=\"color: #0000ff\">return</span>  <span style=\"color: #800080\">$access_token</span><span style=\"color: #000000\">;\n    }\n   \n    \n    </span><span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span><span style=\"color: #000000\"> test(){\n&#x3000;&#x3000;&#x3000;&#x3000;&#x3000;</span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">openid&#x3000;</span>\n        <span style=\"color: #800080\">$touser</span> = <span style=\"color: #800080\">$user_mes</span>[&apos;wx_openid&apos;<span style=\"color: #000000\">];\n        </span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x83B7;&#x53D6;token</span>\n        <span style=\"color: #800080\">$access_token</span> = <span style=\"color: #800080\">$this</span>-&gt;<span style=\"color: #000000\">get_token();\n&#x3000;&#x3000;&#x3000;&#x3000;</span><span style=\"color: #008000\">//</span><span style=\"color: #008000\">&#x6A21;&#x677F;ID</span>\n        <span style=\"color: #800080\">$template_id</span> = &apos;&apos;<span style=\"color: #000000\">; \n        </span><span style=\"color: #800080\">$url</span> = &quot;www.baidu.com&quot;<span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$data</span>=<span style=\"color: #0000ff\">array</span><span style=\"color: #000000\">(\n                </span>&apos;first&apos;=&gt;<span style=\"color: #0000ff\">array</span>(&apos;value&apos;=&gt;<span style=\"color: #008080\">urlencode</span>(&quot;&#x606D;&#x559C;&#x4F60;&#x54E6;&#xFF01;&quot;),&apos;color&apos;=&gt;&quot;#743A3A&quot;),\n                &apos;keyword1&apos;=&gt;<span style=\"color: #0000ff\">array</span>(&apos;value&apos;=&gt;&apos;22&apos;,&apos;color&apos;=&gt;&apos;#EEEEEE&apos;),\n                &apos;keyword2&apos;=&gt;<span style=\"color: #0000ff\">array</span>(&apos;value&apos;=&gt;&apos;33&apos;,&apos;color&apos;=&gt;&apos;#FFFFFF&apos;),\n                &apos;remark&apos;=&gt;<span style=\"color: #0000ff\">array</span>(&apos;value&apos;=&gt;&apos;33&apos;,&apos;color&apos;=&gt;&apos;#FFFFFF&apos;<span style=\"color: #000000\">)\n        );\n        </span><span style=\"color: #800080\">$res</span> = <span style=\"color: #800080\">$this</span>-&gt;Send(<span style=\"color: #800080\">$touser</span>, <span style=\"color: #800080\">$template_id</span>, <span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$data</span>, <span style=\"color: #800080\">$access_token</span><span style=\"color: #000000\">);\n        \n        </span><span style=\"color: #008080\">print_r</span>(<span style=\"color: #800080\">$res</span><span style=\"color: #000000\">);\n    }\n\n       </span><span style=\"color: #008000\">/*</span><span style=\"color: #008000\">*\n     * &#x53D1;&#x9001;&#x81EA;&#x5B9A;&#x4E49;&#x7684;&#x6A21;&#x677F;&#x6D88;&#x606F;\n     * @param $touser  \n     * @param $template_id\n     * @param $url\n     * @param $data\n     * @param string $topcolor\n     * @return bool\n     </span><span style=\"color: #008000\">*/</span>\n    <span style=\"color: #0000ff\">public</span> <span style=\"color: #0000ff\">function</span> Send(<span style=\"color: #800080\">$touser</span>, <span style=\"color: #800080\">$template_id</span>, <span style=\"color: #800080\">$url</span>, <span style=\"color: #800080\">$data</span>, <span style=\"color: #800080\">$access_token</span>, <span style=\"color: #800080\">$topcolor</span> = &apos;#7B68EE&apos;<span style=\"color: #000000\">)\n    {   \n            \n        </span><span style=\"color: #800080\">$template</span> = <span style=\"color: #0000ff\">array</span><span style=\"color: #000000\">(\n            </span>&apos;touser&apos; =&gt; <span style=\"color: #800080\">$touser</span>,  <span style=\"color: #008000\">//</span><span style=\"color: #008000\">openid</span>\n            &apos;template_id&apos; =&gt; <span style=\"color: #800080\">$template_id</span>,\n            &apos;url&apos; =&gt; <span style=\"color: #800080\">$url</span>,\n            &apos;topcolor&apos; =&gt; <span style=\"color: #800080\">$topcolor</span>,\n            &apos;data&apos; =&gt; <span style=\"color: #800080\">$data</span><span style=\"color: #000000\">\n        );\n        </span><span style=\"color: #800080\">$json_template</span> = json_encode(<span style=\"color: #800080\">$template</span><span style=\"color: #000000\">);\n        \n        </span><span style=\"color: #800080\">$url</span> = &quot;https://api.weixin.qq.com/cgi-bin/message/template/send?access_token=&quot;.<span style=\"color: #800080\">$access_token</span><span style=\"color: #000000\">;\n        </span><span style=\"color: #800080\">$dataRes</span> = <span style=\"color: #800080\">$this</span>-&gt;request_post(<span style=\"color: #800080\">$url</span>, <span style=\"color: #008080\">urldecode</span>(<span style=\"color: #800080\">$json_template</span><span style=\"color: #000000\">));\n        </span><span style=\"color: #0000ff\">if</span> (<span style=\"color: #800080\">$dataRes</span>[&apos;errcode&apos;] == 0<span style=\"color: #000000\">) {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #0000ff\">true</span><span style=\"color: #000000\">;\n        } </span><span style=\"color: #0000ff\">else</span><span style=\"color: #000000\"> {\n            </span><span style=\"color: #0000ff\">return</span> <span style=\"color: #0000ff\">false</span><span style=\"color: #000000\">;\n        }\n    }\n    \n    \n\n\n}</span></pre>\n</div>\n<p>&#xA0;</p>');
