an style=\"color: #008080\">getenv</span>(&apos;REMOTE_ADDR&apos;<span style=\"color: #000000\">);\n&#x3000;&#x3000;</span><span style=\"color: #800080\">$ip_</span> = <span style=\"color: #008080\">getenv</span>(&apos;HTTP_X_FORWARDED_FOR&apos;<span style=\"color: #000000\">);\n&#x3000;&#x3000;</span><span style=\"color: #0000ff\">if</span> ((<span style=\"color: #800080\">$ip_</span> != &quot;&quot;) &amp;&amp; (<span style=\"color: #800080\">$ip_</span> != &quot;unknown&quot;<span style=\"color: #000000\">)) {\n&#x3000;&#x3000;&#x3000;&#x3000;</span><span style=\"color: #800080\">$ip</span>=<span style=\"color: #800080\">$ip_</span><span style=\"color: #000000\">;\n&#x3000;&#x3000;}<br></span><span style=\"color: #0000ff\">&#x3000;&#x3000;return</span> <span style=\"color: #800080\">$ip</span><span style=\"color: #000000\">;\n}<br><br><br>\n}\n\n</span>?&gt;</pre>\n</div>\n<p>&#xA0;</p>');
